var Browser=require("zombie");assert=require("chai").assert;var browser;suite("Cross-Page Tests",function(){setup(function(){browser=new Browser}),test("requesting a group rate quote from the hodd river tour page should populate the referrer field",function(e){var r="http://localhost:3000/tours/hood-river";browser.visit(r,function(){browser.clickLink(".requestGroupRate",function(){assert(browser.field("referrer").value===r),e()})})}),test("requesting a group rate from the oregon coast tour page should populate the hidden referrer field correctly",function(e){var r="http://localhost:3000/tours/oregon-coast";browser.visit(r,function(){browser.clickLink(".requestGroupRate",function(){assert(browser.field("referrer").value===r),e()})})}),test('visiting the "request group rate" page dirctly should result in an empty value for the referrer field',function(e){browser.visit("http://localhost:3000/tours/request-group-rate",function(){assert(""===browser.field("referrer").value),e()})})});